mapper.select:
SELECT tcxs.*, case WHEN tcxs.buyer_type= '个人' then mp.person_name WHEN tcxs.buyer_type= '企业' then mc.company_name else '' end as buyer_name, mp_dock.person_name as dock_person_name FROM t_corn_xin_sell tcxs LEFT JOIN m_person mp ON mp.person_id=tcxs.buyer_id LEFT JOIN m_company mc ON mc.company_id=tcxs.buyer_id LEFT JOIN m_person mp_dock ON mp_dock.person_id=tcxs.dock_person_id LEFT JOIN m_company_person mcp ON mcp.company_id=mc.company_id AND mcp.person_id=mp_dock.person_id
AND tcxs.trade_date = #{tradeDate}
AND tcxs.buyer_type = #{buyerType}
AND (mp.person_name LIKE CONCAT('%', #{buyer}, '%') OR mp.alias LIKE CONCAT('%', #{buyer}, '%') OR mp.phone_num LIKE CONCAT('%', #{buyer}, '%') OR mc.company_name LIKE CONCAT('%', #{buyer}, '%') OR mc.company_phone_num LIKE CONCAT('%', #{buyer},'%') OR mp_dock.person_name LIKE CONCAT('%', #{buyer}, '%') OR mp_dock.alias LIKE CONCAT('%', #{buyer}, '%') OR mp_dock.phone_num LIKE CONCAT('%', #{buyer}, '%') OR tcxs.buyer_id = #{buyer} )
AND tcxs.trade_status = #{tradeStatus}
ORDER BY tcxs.trade_date DESC, tcxs.create_time DESC
