mapper.select:
SELECT stds.*, std.table_name, std.table_comment FROM s_table_design_sql stds
LEFT JOIN s_table_design std on stds.table_id = std.table_id


        AND (std.table_name like CONCAT('%', #{tableLike}, '%') OR std.table_comment like CONCAT('%', #{tableLike}, '%'))


        AND stds.sql_type = #{sqlType}


        AND stds.data_status = #{dataStatus}


ORDER BY stds.create_time DESC, stds.execute_order DESC
